using System.Collections; using System.Collections.Generic; using UnityEngine;  public class spawnExplosion : MonoBehaviour {     public GameObject explosion;                 //static float spawnDistance = 10;     // Vector3 spawnPos = barrelPos + barrelDir * spawnDistance;     // Start is called before the first frame update     void Start()     {             }      // Update is called once per frame     void Update()     {         if (barrelExplode.exp)         {             GameObject go = (GameObject)Instantiate(explosion, AnimateOnKeypress.barPos, Quaternion.identity);             Destroy(go, .3f);             barrelExplode.exp = false;         }     } } 